(1687, 27, 'Marriage is a partnership in which each inspires the other, and brings fruition to both of you.', '', 0, 0, '', 'upload', 'no', 'Aniversary', 'none', '0', '0', '', '2022-05-21 16:59:11'),
(1688, 27, 'A good marriage would be between a blind wife and a deaf husband.', '', 0, 0, '', 'upload', 'no', 'Aniversary', 'none', '0', '0', '', '2022-05-21 16:59:22'),
(1689, 27, 'You were made perfectly to be loved - and surely I have loved you, in the idea of you, my whole life long.', '', 0, 0, '', 'upload', 'no', 'Aniversary', 'none', '0', '0', '', '2022-05-21 16:59:33'),
(1690, 27, 'If I had a flower for every time I thought of you...I could walk through my garden forever.', '', 0, 0, '', 'upload', 'no', 'Aniversary', 'none', '0', '0', '', '2022-05-21 16:59:42'),
(1691, 27, 'I love being married. It\'s so great to find that one special person you want to annoy for the rest of your life.', '', 0, 0, '', 'upload', 'no', 'Aniversary', 'none', '0', '0', '', '2022-05-21 16:59:51'),
(1692, 27, 'Bachelors know more about women than married men; if they didn\'t they\'d be married too.', '', 0, 0, '', 'upload', 'no', 'Aniversary', 'none', '0', '0', '', '2022-05-21 17:00:00'),
