(2787, 23, 'If you are not willing to work hard and establish discipline in your life, then all your dreams are merely pipe dreams.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:50:11'),
(2788, 23, 'I\'m still waiting for perfection. In the meantime, I\'ll settle for persistence', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:50:20'),
(2789, 23, 'Recognize and reward players who put the team first, not just the gifted ones. ', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:50:34'),
(2790, 23, 'There is no such thing as life in-between.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:50:43'),
(2791, 23, 'One man cannot make a team.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:50:52'),
(2792, 23, 'Compare where you are to where you want to be, and you\'ll get nowhere', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:51:01'),
(2793, 23, 'A lion never roars after a kill.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2022-05-25 15:51:18'),
