(11231, 23, 'My sister\'s expecting a baby, and I don\'t know if I\'m going to be an uncle or an aunt.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2023-08-27 16:48:20'),
(11232, 23, 'May your adventures bring you closer together, even as they take you far away from home.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2023-08-27 16:48:28'),
(11233, 23, 'The game isn\'t over till the clock says zero.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2023-08-27 16:48:39'),
(11234, 23, 'We\'re out playing ball in the gym, I put boogers on the basketball and pass it to him.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2023-08-27 16:48:48'),
(11235, 23, 'Sports teach you how to be quick. Injuries teach you how to slow down.', '', 0, 0, '', 'upload', 'no', 'AAABasketball', 'none', '0', '0', '', '2023-08-27 16:48:59'),
